package HW02;

public class Problem1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double num = 3.5;
		double secondPower = (3.5*3.5); //second power
		double thirdPower = (3.5*3.5*3.5); // third power
		double fourthPower = Math.pow(3.5,4); // fourth power
		
		System.out.println("3.5 to the second power = " + secondPower);
		System.out.println("3.5 to the third power =  " + thirdPower);
		System.out.println("3.5 to the fourth power = " + fourthPower);
	}

} 

// prints 3.5 to the 2nd, 3rd, and 4th power
