package Lab03and04_FundamentalDataTypes;

public class Strings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String puzzle = "crossword";
		
		System.out.println(puzzle.charAt(0));
		System.out.println(puzzle.substring(0,5));
		System.out.println(puzzle.substring(0,1)+puzzle.charAt(5));
	}

}
