package Lab10_NestedForLoops_WhileLoops;

public class JUNK1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String word = "Woo";
		
		for (int j = 1; j<=10; j++)
		{ // open 
		
			for (int i = 1; i<=8; i++)
				{
					System.out.print(word);
				}
		
		System.out.println(); // moves it down a line
		
			
		} // close
		
	}

}
