package Lab10_NestedForLoops_WhileLoops;

public class junkwhile {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int count = 1; // count starts at 1
		System.out.println("numbers 1 - 10");
		
		while (count <= 10) //while statement is true
		{ // need brackets for more than two statements
			System.out.println(count); // prints count 
			count++; // increase count by 1
		}
		
	}

}//print numbers 1-10

